import React, { useEffect, useState, useRef, useContext } from 'react';
import { StyleSheet, Text, View, Dimensions, Image, TextInput } from 'react-native';
import { ScrollView } from 'react-native-gesture-handler';
import LoadingBar from '../components/LoadingBar';
import Screen from '../components/Screen';
import WaterIntakeDetail from '../components/WaterIntakeDetail';
import waterstatistics from '../database/waterstatistics.json';
import CountDown from 'react-native-countdown-component';
import Button from '../components/Button';
import StartButton from '../components/StartButton';
import api from '../api/api';
import endpoints from '../api/endpoints';

function TaskDetailScreen({ route }) {

    const { activityName } = route.params;
    const [timer, setTimer] = useState(false);
    const [time, setTime] = useState(0);
    var weekly_intake = [];
    var water_count = 0;
    var points = [];
    var days = [];
    var sum = 0;
    var average = 0;

    useEffect(() => {
        let timer = setInterval(() => console.log('fire!'), 1000);
        return () => clearInterval(timer)
    }, [])
    if (waterstatistics != undefined) {
        waterstatistics.map((stats, index) => {
            stats.weekly_percentage.map((data, key) => {
                water_count += 1;
                weekly_intake.push({
                    id: water_count,
                    date: data.date,
                    day_percentage: data.day_percentage,
                    day: data.day,
                    oz: data.oz,
                }
                );

                days.push(data.date.toString().substring(0, 5));
                points.push(data.day_percentage);
                sum += data.day_percentage;
            })
        })
    }
    const handleTimerSubmit = async () => {

        //let apiStr = endpoints.login + "{" + values.email + "}/{" + values.password +"}"
        let apiStr = endpoints.saveTime;
        api.baseURL.post(apiStr, { activityName: values.activityName + "," + authContext.user.hashID, schedule: values.schedule, status: false, time: values.time, hashID:authContext.user.hashID}).then(response => {
            if (response.data != null) {
                setModalVisible(!isModalVisible);
            }
        });
    }
    const startStopTimer = () => {
        setTimer(!timer);
        if (timer){
            Alert.alert(
                "Login Failed",
                "Invalid email or password",
                [
                  {
                    text: "Save",
                    onPress: () => console.log("Cancel Pressed"),
                    style: "cancel"
                  },
                  { text: "Delete", onPress: () => console.log("OK Pressed") }
                ]
              );
        }

    }
    return (
        <Screen>

            <View style={{ alignItems: 'center', margin: 20 }}>
                <StartButton title="Start Timer" onPress={startStopTimer} isPressed={timer} />
            </View>

            <CountDown
                size={30}
                until={1000}
                onFinish={() => alert('Finished')}
                digitStyle={{ backgroundColor: '#FFF', borderWidth: 2, borderColor: '#3B97ED' }}
                digitTxtStyle={{ color: '#1CC625' }}
                timeLabelStyle={{ color: 'red', fontWeight: 'bold' }}
                separatorStyle={{ color: '#1CC625' }}
                timeToShow={['H', 'M', 'S']}
                timeLabels={{ m: null, s: null }}
                showSeparator
                running={timer}
                onChange={(time) => {
                    setTime(time);
                }}
            >

            </CountDown>
            <ScrollView>
                <View style={styles.dataContainer}>
                    <View style={{ marginLeft: 5, flexDirection: 'row', alignItems: 'center' }}>
                        <View>
                            <Text style={styles.text}> Time Data </Text>
                            <Text style={styles.text4}> See your water intake in details </Text>
                        </View>
                    </View>
                    {
                        weekly_intake.map((data, key) => {
                            return < WaterIntakeDetail key={key} date={data.date} percentage={data.day_percentage} />
                        })
                    }
                </View>

            </ScrollView>

        </Screen>
    );
}
const styles = StyleSheet.create({
    homeContainer: {
        height: '100%',
        width: '100%',
    },
    loadingBarContainer: {
        width: '100%',
        alignItems: 'center',
    },
    dataContainer: {
        marginTop: 10,
        width: '95%',
        backgroundColor: 'white',
        alignSelf: 'center',
        borderRadius: 20,
    },
    text: {
        fontSize: 20,
        color: 'black',
        fontFamily: 'Avenir-Medium'
    },
    text2: {
        fontSize: 20,
        color: 'white',
        fontFamily: 'Avenir-Medium'
    },
    text4: {
        fontSize: 14,
        color: 'gray',
        fontFamily: 'Avenir-Book'
    },
    exportText: {
        fontSize: 20,
        color: 'blue',
        fontFamily: 'Avenir-Medium'
    },
    exportText1: {
        fontSize: 14,
        color: 'blue',
        fontFamily: 'Avenir-Medium'
    },
    text5: {
        fontSize: 20,
        color: 'gray',
        fontFamily: 'Avenir-Medium'
    }
});

export default TaskDetailScreen;
